mapper.update:
UPDATE LANGUAGE_CERTIFICATE
SET LANGUAGE_CERTIFICATE_NAME = #{LANGUAGE_CERTIFICATE_NAME},
LANGUAGE_CERTIFICATE_GRADE = #{LANGUAGE_CERTIFICATE_GRADE}
WHERE LANGUAGE_CERTIFICATE_SEQ = #{LANGUAGE_CERTIFICATE_SEQ}
AND  LANGUAGE_SEQ = #{LANGUAGE_SEQ}

mapper.delete:
DELETE FROM LANGUAGE_CERTIFICATE
WHERE LANGUAGE_SEQ = #{LANGUAGE_SEQ}
AND LANGUAGE_CERTIFICATE_SEQ = #{LANGUAGE_CERTIFICATE_SEQ}
