mapper.update:
UPDATE PROJECT
SET PROJECT_NAME = #{PROJECT_NAME}, PROJECT_YEAR = #{PROJECT_YEAR}, PROJECT_ONELINE_EX = #{PROJECT_ONELINE_EX},
PROJECT_TEAM = #{PROJECT_TEAM}, PROJECT_MEMBER = #{PROJECT_MEMBER}, PROJECT_USE_STACK = #{PROJECT_USE_STACK},
PROJECT_DETAIL = #{PROJECT_DETAIL}, PROJECT_STORAGE_LINK = #{PROJECT_STORAGE_LINK}, 
PROJECT_WEBSITE = #{PROJECT_WEBSITE}, PROJECT_ANDROID = #{PROJECT_ANDROID} ,PROJECT_IOS=#{PROJECT_IOS}
WHERE PROJECT_SEQ = #{PROJECT_SEQ} AND PROJECT_MEMBER_CODE = #{PROJECT_MEMBER_CODE}

mapper.delete:
DELETE FROM PROJECT
WHERE PROJECT_SEQ = #{PROJECT_SEQ};
AND PROJECT_PROFILE_SEQ = #{PROJECT_MEMBER_CODE}
